mapper.select:


SELECT
	b_num, to_char(b_date, 'YYYY-MM-DD') as b_date, b_indate, b_outdate, b_ismember, b_name, b_phone, b_email, b_payment, b_state, m_no, rg_grade
FROM (
		select /*+ INDEX_DESC(spring_board spring_board_pk)*/
			rownum as rnum, b_num, b_date, b_indate, b_outdate, b_ismember, b_name, b_phone, b_email, b_payment, b_state, m_no, rg_grade
		FROM booking
		WHERE
		
			
		
		 rownum <= #{pageNum} * #{amount}
	) bookList
	WHERE rnum > (#{pageNum} - 1) * #{amount}
