mapper.select:
/* Reply - replyList */
SELECT r_num, b_num, r_name, r_content, r_pwd, to_char(r_date, 'YYYY-MM-DD HH24:MI:SS') as r_date
FROM reply
WHERE qa_num = #{qa_num}
ORDER BY r_num desc

mapper.insert:
/* Reply - replyInsert */
SELECT reply_seq.nextval FROM dual
INSERT INTO reply(qa_num, r_num, r_name, r_content, r_pwd) VALUES(#{qa_num}, #{r_num}, #{r_name}, #{r_content}, #{r_pwd})

mapper.select:
/* Reply - pwdConfirm */
SELECT NVL((
			SELECT 1 FROM reply
			WHERE r_num = #{r_num} AND r_pwd = #{r_pwd}
			), 0) as state
FROM dual

mapper.update:

/* Reply - replyUpdate */
UPDATE reply 
SET r_content = #{r_content}
, r_date = sysdate

	, r_pwd = #{r_pwd}

WHERE r_num = #{r_num}

mapper.select:
SELECT r_num, qa_num, r_name, r_content, r_date
FROM reply
where r_num = #{r_num}
